import React from 'react';
import styled from 'styled-components';

import { fetchItunes } from '../utility/searchApi';

const Wrapper = styled.form`
  position: relative;
  display: flex;
  min-width: 60vw;
  max-width: 720px;
  margin-bottom: 1rem;

  .search-icon {
    position: absolute;
    top: 10px;
    left: 10px;
    width: 18px;
  }

  .clear-icon {
    position: absolute;
    top: 12px;
    right: 140px;
    width: 15px;
    cursor: pointer;
  }

  .search-input {
    border: 1px solid grey;
    border-radius: 5px;
    font-size: 1.2rem;
    height: 2rem;
    width: 100%;
    padding: 2px 23px 2px 45px;
    outline: 0;
    background-color: var(--white);
    

    :hover,
    :focus {
      border: 1.5px solid var(--primary);
      background-color: white;
    }
  }
  .search-button {
    
    border: 0;
    border-radius: 5px;
    font-size: 1.2rem;
    padding: 0 2rem;
    outline: 0;
    background-color: var(--primary);
    color: var(--white);
    cursor: pointer;

  }
`;

const Search = ({
  inputValue,
  setInputValue,
  setResult,
  setLoading,
  setSearched,
  setFilter
}) => {
  const handleChange = (e) => {
    setInputValue(e.target.value);
  };

  const handleClear = () => {
    setInputValue('');
    setSearched(false);
    setResult([]);
    setFilter('all');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSearched(true);
    setResult([]);
    setLoading(true);

    const str = inputValue;

    const result = await fetchItunes(str);

    setResult([...result]);

    setLoading(false);
    setFilter('all');
  };

  return (
    <div className="search">
      <Wrapper className="search-input" onSubmit={handleSubmit}>
        <img
          className="search-icon"
          alt="search-icon"
          src="data:image/svg+xml;utf8;base64,"
        />
        <input
          className="search-input"
          placeholder="Search"
          type="text"
          value={inputValue}
          onChange={handleChange}
        />
        {inputValue !== '' && (
          <img
            className="clear-icon"
            alt="clear-icon"
            src="data:image/svg+xml;utf8;base64,"
            onClick={handleClear}
          />
        )}
      <button className="search-button" type="button" onClick={handleSubmit}>
        Search
      </button>
      </Wrapper>
    </div>
  );
};

export default Search;
